package com.school.modules.repositories;

import org.springframework.data.jpa.repository.JpaRepository;
import com.school.modules.dtos.Module;

import java.util.List;
import java.util.Optional;

public interface ModuleRepository extends JpaRepository<Module, Long> {

	// Method names define the 'SQL' that is generated by spring.
	List<Module> findById(Optional<Integer> id);

	Optional<Module> findByModuleCode(String moduleCode);

}
